import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  Image,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Alert,
  ActivityIndicator
} from 'react-native';
import { useCart } from '../../contexts/CartContext';
import api from '../../services/api';

export default function ProductScreen({ route, navigation }) {
  const { productId, product: initialProduct, isNewProduct } = route.params;
  const [product, setProduct] = useState(initialProduct || null);
  const [loading, setLoading] = useState(!initialProduct);
  const [quantity, setQuantity] = useState(1);
  const { addToCart } = useCart();

  useEffect(() => {
    if (productId && !initialProduct) {
      fetchProductDetails();
    }
  }, [productId]);

  const fetchProductDetails = async () => {
    try {
      setLoading(true);
      const response = await api.get(`/products/${productId}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Erreur lors de la récupération du produit:', error);
      Alert.alert('Erreur', 'Impossible de charger les détails du produit');
      navigation.goBack();
    } finally {
      setLoading(false);
    }
  };

  const handleQuantityChange = (change) => {
    const newQuantity = quantity + change;
    if (newQuantity >= 1 && newQuantity <= product.stock) {
      setQuantity(newQuantity);
    }
  };

  const handleAddToCart = () => {
    try {
      addToCart(product, quantity);
      Alert.alert(
        'Succès',
        'Produit ajouté au panier',
        [
          {
            text: 'Continuer les achats',
            onPress: () => navigation.goBack(),
            style: 'cancel'
          },
          {
            text: 'Voir le panier',
            onPress: () => navigation.navigate('Cart')
          }
        ]
      );
    } catch (error) {
      Alert.alert('Erreur', 'Impossible d\'ajouter le produit au panier');
    }
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#10B981" />
      </View>
    );
  }

  if (!product) {
    return (
      <View style={styles.errorContainer}>
        <Text style={styles.errorText}>Produit non trouvé</Text>
        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.goBack()}
        >
          <Text style={styles.buttonText}>Retour</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <ScrollView>
        <Image
          source={{ uri: product.imageUrl }}
          style={styles.image}
          resizeMode="cover"
        />

        <View style={styles.contentContainer}>
          <Text style={styles.name}>{product.name}</Text>
          <Text style={styles.price}>{product.price}€</Text>

          {product.promotion && (
            <View style={styles.promotionContainer}>
              <Text style={styles.oldPrice}>{product.originalPrice}€</Text>
              <Text style={styles.promotionText}>
                -{product.promotionPercentage}%
              </Text>
            </View>
          )}

          <Text style={styles.description}>{product.description}</Text>

          <View style={styles.nutritionContainer}>
            <Text style={styles.sectionTitle}>Informations nutritionnelles</Text>
            <View style={styles.nutritionGrid}>
              <View style={styles.nutritionItem}>
                <Text style={styles.nutritionLabel}>Calories</Text>
                <Text style={styles.nutritionValue}>
                  {product.nutritionalInfo?.calories || '-'} kcal
                </Text>
              </View>
              <View style={styles.nutritionItem}>
                <Text style={styles.nutritionLabel}>Protéines</Text>
                <Text style={styles.nutritionValue}>
                  {product.nutritionalInfo?.proteins || '-'} g
                </Text>
              </View>
              <View style={styles.nutritionItem}>
                <Text style={styles.nutritionLabel}>Glucides</Text>
                <Text style={styles.nutritionValue}>
                  {product.nutritionalInfo?.carbs || '-'} g
                </Text>
              </View>
              <View style={styles.nutritionItem}>
                <Text style={styles.nutritionLabel}>Lipides</Text>
                <Text style={styles.nutritionValue}>
                  {product.nutritionalInfo?.fats || '-'} g
                </Text>
              </View>
            </View>
          </View>

          {product.allergens && product.allergens.length > 0 && (
            <View style={styles.allergensContainer}>
              <Text style={styles.sectionTitle}>Allergènes</Text>
              <Text style={styles.allergensText}>
                {product.allergens.join(', ')}
              </Text>
            </View>
          )}
        </View>
      </ScrollView>

      <View style={styles.footer}>
        <View style={styles.quantityContainer}>
          <TouchableOpacity
            onPress={() => handleQuantityChange(-1)}
            style={styles.quantityButton}
          >
            <Text style={styles.quantityButtonText}>-</Text>
          </TouchableOpacity>

          <Text style={styles.quantity}>{quantity}</Text>

          <TouchableOpacity
            onPress={() => handleQuantityChange(1)}
            style={styles.quantityButton}
          >
            <Text style={styles.quantityButtonText}>+</Text>
          </TouchableOpacity>
        </View>

        <TouchableOpacity
          style={[
            styles.addToCartButton,
            (!product.stock || product.stock < 1) && styles.disabledButton
          ]}
          onPress={handleAddToCart}
          disabled={!product.stock || product.stock < 1}
        >
          <Text style={styles.addToCartButtonText}>
            {!product.stock || product.stock < 1
              ? 'Rupture de stock'
              : 'Ajouter au panier'}
          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

